import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import { useMutation, useQueryClient } from 'react-query';
import axios from 'axios';
import { toast } from 'react-hot-toast';
import { format } from 'date-fns';
import { ArrowLeft, DollarSign, Calendar, FileText, Tag, TrendingUp, TrendingDown } from 'lucide-react';

const AddTransaction = () => {
  const navigate = useNavigate();
  const queryClient = useQueryClient();
  const [transactionType, setTransactionType] = useState('expense');

  const {
    register,
    handleSubmit,
    formState: { errors },
    watch,
    reset
  } = useForm({
    defaultValues: {
      type: 'expense',
      amount: '',
      description: '',
      category: '',
      date: format(new Date(), 'yyyy-MM-dd')
    }
  });

  const watchType = watch('type');

  const createTransactionMutation = useMutation(
    (transactionData) => axios.post('/transactions', transactionData),
    {
      onSuccess: () => {
        queryClient.invalidateQueries(['transactions']);
        queryClient.invalidateQueries(['dashboard']);
        toast.success('Transaction added successfully');
        navigate('/transactions');
      },
      onError: (error) => {
        toast.error(error.response?.data?.message || 'Failed to add transaction');
      }
    }
  );

  const onSubmit = (data) => {
    createTransactionMutation.mutate({
      ...data,
      amount: parseFloat(data.amount)
    });
  };

  const incomeCategories = [
    { value: 'salary', label: 'Salary' },
    { value: 'freelance', label: 'Freelance' },
    { value: 'investment', label: 'Investment' },
    { value: 'business', label: 'Business' },
    { value: 'gift', label: 'Gift' },
    { value: 'other_income', label: 'Other Income' }
  ];

  const expenseCategories = [
    { value: 'food', label: 'Food & Dining' },
    { value: 'transportation', label: 'Transportation' },
    { value: 'housing', label: 'Housing' },
    { value: 'utilities', label: 'Utilities' },
    { value: 'entertainment', label: 'Entertainment' },
    { value: 'healthcare', label: 'Healthcare' },
    { value: 'shopping', label: 'Shopping' },
    { value: 'education', label: 'Education' },
    { value: 'travel', label: 'Travel' },
    { value: 'insurance', label: 'Insurance' },
    { value: 'other_expense', label: 'Other Expense' }
  ];

  const categories = watchType === 'income' ? incomeCategories : expenseCategories;

  return (
    <div className="max-w-2xl mx-auto">
      {/* Header */}
      <div className="flex items-center space-x-4 mb-8">
        <button
          onClick={() => navigate('/transactions')}
          className="p-2 text-gray-400 hover:text-gray-600 rounded-lg hover:bg-gray-100 transition-colors duration-200"
        >
          <ArrowLeft className="h-6 w-6" />
        </button>
        <h1 className="text-3xl font-bold text-gray-900">Add Transaction</h1>
      </div>

      {/* Form */}
      <div className="card">
        <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
          {/* Transaction Type */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-3">
              Transaction Type
            </label>
            <div className="grid grid-cols-2 gap-4">
              <label className="relative">
                <input
                  type="radio"
                  value="income"
                  {...register('type', { required: 'Transaction type is required' })}
                  className="sr-only"
                  onChange={(e) => setTransactionType(e.target.value)}
                />
                <div className={`cursor-pointer p-4 rounded-lg border-2 transition-all duration-200 ${
                  watchType === 'income'
                    ? 'border-success-500 bg-success-50'
                    : 'border-gray-200 hover:border-gray-300'
                }`}>
                  <div className="text-center">
                    <div className={`mx-auto mb-2 w-8 h-8 rounded-full flex items-center justify-center ${
                      watchType === 'income' ? 'bg-success-500' : 'bg-gray-400'
                    }`}>
                      <DollarSign className="h-4 w-4 text-white" />
                    </div>
                    <span className={`font-semibold ${
                      watchType === 'income' ? 'text-success-700' : 'text-gray-700'
                    }`}>
                      Income
                    </span>
                  </div>
                </div>
              </label>

              <label className="relative">
                <input
                  type="radio"
                  value="expense"
                  {...register('type', { required: 'Transaction type is required' })}
                  className="sr-only"
                  onChange={(e) => setTransactionType(e.target.value)}
                />
                <div className={`cursor-pointer p-4 rounded-lg border-2 transition-all duration-200 ${
                  watchType === 'expense'
                    ? 'border-danger-500 bg-danger-50'
                    : 'border-gray-200 hover:border-gray-300'
                }`}>
                  <div className="text-center">
                    <div className={`mx-auto mb-2 w-8 h-8 rounded-full flex items-center justify-center ${
                      watchType === 'expense' ? 'bg-danger-500' : 'bg-gray-400'
                    }`}>
                      <DollarSign className="h-4 w-4 text-white" />
                    </div>
                    <span className={`font-semibold ${
                      watchType === 'expense' ? 'text-danger-700' : 'text-gray-700'
                    }`}>
                      Expense
                    </span>
                  </div>
                </div>
              </label>
            </div>
            {errors.type && (
              <p className="mt-1 text-sm text-danger-600">{errors.type.message}</p>
            )}
          </div>

          {/* Amount */}
          <div>
            <label htmlFor="amount" className="block text-sm font-medium text-gray-700 mb-2">
              Amount
            </label>
            <div className="relative">
              <DollarSign className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
              <input
                type="number"
                id="amount"
                step="0.01"
                min="0"
                placeholder="0.00"
                {...register('amount', {
                  required: 'Amount is required',
                  min: { value: 0.01, message: 'Amount must be greater than 0' }
                })}
                className={`input-field pl-10 ${errors.amount ? 'border-danger-300 focus:border-danger-500' : ''}`}
              />
            </div>
            {errors.amount && (
              <p className="mt-1 text-sm text-danger-600">{errors.amount.message}</p>
            )}
          </div>

          {/* Description */}
          <div>
            <label htmlFor="description" className="block text-sm font-medium text-gray-700 mb-2">
              Description
            </label>
            <div className="relative">
              <FileText className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
              <input
                type="text"
                id="description"
                placeholder="Enter transaction description"
                {...register('description', {
                  required: 'Description is required',
                  minLength: { value: 3, message: 'Description must be at least 3 characters' }
                })}
                className={`input-field pl-10 ${errors.description ? 'border-danger-300 focus:border-danger-500' : ''}`}
              />
            </div>
            {errors.description && (
              <p className="mt-1 text-sm text-danger-600">{errors.description.message}</p>
            )}
          </div>

          {/* Category */}
          <div>
            <label htmlFor="category" className="block text-sm font-medium text-gray-700 mb-2">
              Category
            </label>
            <div className="relative">
              <Tag className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
              <select
                id="category"
                {...register('category', { required: 'Category is required' })}
                className={`input-field pl-10 ${errors.category ? 'border-danger-300 focus:border-danger-500' : ''}`}
              >
                <option value="">Select a category</option>
                {categories.map((category) => (
                  <option key={category.value} value={category.value}>
                    {category.label}
                  </option>
                ))}
              </select>
            </div>
            {errors.category && (
              <p className="mt-1 text-sm text-danger-600">{errors.category.message}</p>
            )}
          </div>

          {/* Date */}
          <div>
            <label htmlFor="date" className="block text-sm font-medium text-gray-700 mb-2">
              Date
            </label>
            <div className="relative">
              <Calendar className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
              <input
                type="date"
                id="date"
                {...register('date', { required: 'Date is required' })}
                className={`input-field pl-10 ${errors.date ? 'border-danger-300 focus:border-danger-500' : ''}`}
              />
            </div>
            {errors.date && (
              <p className="mt-1 text-sm text-danger-600">{errors.date.message}</p>
            )}
          </div>

          {/* Submit Buttons */}
          <div className="flex space-x-4 pt-6">
            <button
              type="button"
              onClick={() => navigate('/transactions')}
              className="btn-secondary flex-1"
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={createTransactionMutation.isLoading}
              className="btn-primary flex-1 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {createTransactionMutation.isLoading ? 'Adding...' : 'Add Transaction'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddTransaction;
